import React, { useState } from 'react';
import axios from 'axios';
import './ApplyForm.css';

function ApplyForm() {
  const [formData, setFormData] = useState({ name: '', aadhar: '', address: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/apply', formData);
      alert('Application submitted successfully!');
    } catch (error) {
      alert('Submission failed.');
    }
  };

  return (
    <div className="apply-container">
      <div className="overlay"></div>
      <div className="apply-card">
        <h1 className="apply-title">Empower Your Home with Ujjwala Yojana</h1>
        <p className="apply-subtext">Fill out the form to apply for LPG connection under PMUY.</p>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label>Name:</label>
            <input type="text" name="name" className="form-control" onChange={handleChange} required />
          </div>
          <div className="mb-3">
            <label>Aadhar Number:</label>
            <input type="text" name="aadhar" className="form-control" onChange={handleChange} required />
          </div>
          <div className="mb-3">
            <label>Address:</label>
            <input type="text" name="address" className="form-control" onChange={handleChange} required />
          </div>
          <button type="submit" className="btn-submit">Submit Application</button>
        </form>
      </div>
    </div>
  );
}

export default ApplyForm;
