import React, { useState } from 'react';

export default function ApplyPage() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    aadhar: '',
    address: '',
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const res = await fetch('https://your-backend-api/pmuy/apply', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        setMessage('Application submitted successfully!');
        setFormData({
          name: '',
          email: '',
          phone: '',
          aadhar: '',
          address: '',
        });
      } else {
        const data = await res.json();
        setMessage(`Error: ${data.message || 'Failed to submit'}`);
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }

    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Your existing inputs - keep same UI */}
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
      <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" required type="email" />
      <input name="phone" value={formData.phone} onChange={handleChange} placeholder="Phone" required />
      <input name="aadhar" value={formData.aadhar} onChange={handleChange} placeholder="Aadhar" required />
      <input name="address" value={formData.address} onChange={handleChange} placeholder="Address" required />
      <button type="submit" disabled={loading}>{loading ? 'Submitting...' : 'Submit'}</button>
      {message && <p>{message}</p>}
    </form>
  );
}
