import React, { useState } from 'react';
import axios from 'axios';

function CheckStatus() {
  const [aadhar, setAadhar] = useState('');
  const [status, setStatus] = useState(null);

  const handleCheck = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/status/${aadhar}`);
      setStatus(response.data);
    } catch {
      alert('Network Error! Make sure backend is running.');
    }
  };

  return (
    <div className="form-wrapper">
      <div className="card shadow p-4">
        <h3 className="text-center mb-4">Check Your Application Status</h3>
        <input
          type="text"
          className="form-control mb-3"
          placeholder="Enter Aadhar Number"
          value={aadhar}
          onChange={(e) => setAadhar(e.target.value)}
        />
        <button className="btn gradient-btn w-100 mb-3" onClick={handleCheck}>Check Status</button>

        {status && (
          <div className="status-box">
            <p><strong>Status:</strong>
              <span className={`status-badge ${status.applicationStatus.toLowerCase()}`}>
                {status.applicationStatus}
              </span>
            </p>
            {status.reason && <p><strong>Reason:</strong> {status.reason}</p>}
          </div>
        )}
      </div>
    </div>
  );
}

export default CheckStatus;
