import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import pmuyLogo from '../assets/pumy-logo.png';

const Home = () => {
  return (
    <div className="home-wrapper text-center">
      <img src={pmuyLogo} alt="PMUY Logo" className="logo mb-4" />
      <h1 className="fw-bold text-orange">Welcome to PMUY Portal</h1>
      <p className="lead mb-5">Apply for LPG connection subsidy under Pradhan Mantri Ujjwala Yojana.</p>

      <div className="d-flex justify-content-center gap-3">
        <Link to="/apply" className="btn btn-lg gradient-btn">Apply Now</Link>
        <Link to="/status" className="btn btn-lg btn-outline-dark">Check Status</Link>
      </div>
    </div>
  );
};

export default Home;
