import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; // optional custom styles

function Home() {
  return (
    <div className="container text-center py-5">
      <img
        src="https://i.postimg.cc/JhFQnBSx/pmuy-logo.png"
        alt="PMUY Logo"
        className="mb-4"
        style={{ width: '200px' }}
      />
      <h1 className="fw-bold text-danger">Pradhan Mantri Ujjwala Yojana</h1>
      <p className="lead">Get LPG connection with government subsidy</p>

      <div className="d-flex justify-content-center gap-3 mt-4">
        <Link to="/apply" className="btn btn-primary btn-lg">
          Apply Now
        </Link>
        <Link to="/admin" className="btn btn-outline-dark btn-lg">
          Admin Login
        </Link>
      </div>
    </div>
  );
}

export default Home;
