import React, { useState } from 'react';
import axios from 'axios';

function ApplyForm() {
  const [formData, setFormData] = useState({ name: '', aadhar: '', income: '', address: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/apply', formData);
      alert('Application submitted successfully!');
    } catch (error) {
      alert('Submission failed.');
    }
  };

  return (
    <div className="glass-card">
      <h2 className="text-center mb-4">Apply for PMUY</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Full Name" className="form-control mb-3" onChange={handleChange} required />
        <input type="text" name="aadhar" placeholder="Aadhar Number" className="form-control mb-3" onChange={handleChange} required />
        <input type="number" name="income" placeholder="Annual Income" className="form-control mb-3" onChange={handleChange} required />
        <textarea name="address" placeholder="Address" className="form-control mb-3" onChange={handleChange} required />
        <button type="submit" className="btn gradient-btn w-100">Submit</button>
      </form>
    </div>
  );
}

export default ApplyForm;
