import React, { useState } from 'react';
import axios from 'axios';

function ApplyForm() {
  const [formData, setFormData] = useState({ name: '', aadhar: '', income: '', address: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/apply', formData);
      alert('Application submitted successfully!');
    } catch (error) {
      alert('Submission failed.');
    }
  };

  return (
    <div className="card p-4 shadow">
      <h2 className="mb-4 text-center">Apply for PMUY</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Name:</label>
          <input type="text" name="name" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Aadhar Number:</label>
          <input type="text" name="aadhar" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Annual Income:</label>
          <input type="number" name="income" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Address:</label>
          <textarea name="address" className="form-control" onChange={handleChange} required />
        </div>
        <button type="submit" className="btn gradient-btn w-100">Submit Application</button>
      </form>
    </div>
  );
}

export default ApplyForm;
