// File: src/components/Home.js

import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import { FaRegAddressCard, FaTools } from 'react-icons/fa';
import pmuyLogo from '../assets/pmuy-logo.png'; // Save your logo in src/assets folder

const Home = () => {
  return (
    <div className="home-wrapper">
      <div className="home-container text-center">
        <img src={pmuyLogo} alt="PMUY Logo" className="logo" />
        <h1 className="mt-4 fw-bold">Welcome to PMUY Portal</h1>
        <p className="lead">Apply for Ujjwala Yojana scheme easily using this portal.</p>

        <div className="button-group mt-5">
          <Link to="/apply" className="btn btn-primary btn-lg me-3">
            <FaRegAddressCard className="me-2" /> Apply Now
          </Link>
          <Link to="/admin" className="btn btn-outline-light btn-lg">
            <FaTools className="me-2" /> Admin Panel
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
