import React, { useState } from 'react';
import axios from 'axios';
import './ApplyForm.css';

function ApplyForm() {
  const [formData, setFormData] = useState({ name: '', aadhar: '', address: '', income: '' });

  const handleChange = (e) => {
    const value = e.target.name === 'income' ? Number(e.target.value) : e.target.value;
    setFormData({ ...formData, [e.target.name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/users/apply', formData);
      alert(res.data.message);
    } catch (error) {
      if (error.response && error.response.data && error.response.data.message) {
        alert('Submission failed: ' + error.response.data.message);
      } else {
        alert('Submission failed. Please check your details and try again.');
      }
    }
  };

  return (
    <div className="apply-container">
      <div className="overlay"></div>
      <div className="apply-card">
        <h1 className="apply-title">Empower Your Home with Ujjwala Yojana</h1>
        <p className="apply-subtext">Fill the form to apply for LPG benefits under PMUY.</p>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name</label>
            <input type="text" name="name" onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Aadhar Number</label>
            <input type="text" name="aadhar" onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Address</label>
            <input type="text" name="address" onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Annual Income</label>
            <input type="number" name="income" onChange={handleChange} required />
          </div>
          <button type="submit" className="btn-submit">Submit Application</button>
        </form>
      </div>
    </div>
  );
}

export default ApplyForm;
