import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPanel() {
  const [applications, setApplications] = useState([]);

  const fetchData = async () => {
    const response = await axios.get('http://localhost:5000/applications');
    setApplications(response.data);
  };

  const handleAction = async (id, action) => {
    await axios.patch(`http://localhost:5000/${action}/${id}`);
    fetchData();
  };

  useEffect(() => { fetchData(); }, []);

  return (
    <div className="form-wrapper">
      <div className="card shadow p-4">
        <h3 className="text-center mb-4">Admin Panel</h3>
        <table className="table table-bordered table-hover">
          <thead className="table-light">
            <tr>
              <th>Name</th>
              <th>Aadhar</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {applications.map(app => (
              <tr key={app._id}>
                <td>{app.name}</td>
                <td>{app.aadhar}</td>
                <td><span className={`badge ${app.status === 'Approved' ? 'bg-success' : app.status === 'Rejected' ? 'bg-danger' : 'bg-warning'}`}>{app.status}</span></td>
                <td>
                  <button className="btn btn-sm btn-success me-2" onClick={() => handleAction(app._id, 'approve')}>Approve</button>
                  <button className="btn btn-sm btn-danger" onClick={() => handleAction(app._id, 'reject')}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPanel;
