// src/components/Home.js
import React from "react";
import { Link } from "react-router-dom";
import './Home.css';

function Home() {
  return (
    <div className="home-container">
      <h1 className="home-heading">Welcome to PMUY Portal</h1>
      <p className="home-subtext">
        Apply for Ujjwala Yojana scheme easily using this official government portal.
      </p>
      <Link to="/apply">
        <button className="apply-button">Apply Now</button>
      </Link>
    </div>
  );
}

export default Home;
