import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPanel() {
  const [applications, setApplications] = useState([]);

  const fetchData = async () => {
    const response = await axios.get('http://localhost:5000/applications');
    setApplications(response.data);
  };

  const handleAction = async (id, action) => {
    await axios.patch(`http://localhost:5000/${action}/${id}`);
    fetchData();
  };

  useEffect(() => { fetchData(); }, []);

  return (
    <div className="card p-4 shadow">
      <h2 className="mb-4 text-center">Admin Panel</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Name</th>
            <th>Aadhar</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {applications.map(app => (
            <tr key={app._id}>
              <td>{app.name}</td>
              <td>{app.aadhar}</td>
              <td>{app.status}</td>
              <td>
                <button className="btn btn-success btn-sm me-2" onClick={() => handleAction(app._id, 'approve')}>Approve</button>
                <button className="btn btn-danger btn-sm" onClick={() => handleAction(app._id, 'reject')}>Reject</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
