import React, { useState } from 'react';
import axios from 'axios';

function CheckStatus() {
  const [aadhar, setAadhar] = useState('');
  const [status, setStatus] = useState(null);

  const handleCheck = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/status/${aadhar}`);
      setStatus(response.data);
    } catch (error) {
      alert('Error fetching status');
    }
  };

  return (
    <div className="card p-4 shadow">
      <h2 className="mb-4 text-center">Check Application Status</h2>
      <input type="text" placeholder="Enter Aadhar Number" className="form-control mb-3" value={aadhar} onChange={(e) => setAadhar(e.target.value)} />
      <button className="btn gradient-btn w-100 mb-3" onClick={handleCheck}>Check Status</button>
      {status && (
        <div className="alert alert-info">
          Status: <b>{status.applicationStatus}</b><br />
          {status.reason && `Reason: ${status.reason}`}
        </div>
      )}
    </div>
  );
}

export default CheckStatus;
