import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  return (
    <div className="home-hero text-center">
      <h1 className="hero-title">Pradhan Mantri Ujjwala Yojana</h1>
      <p className="hero-subtext">Apply for LPG connection subsidy easily through our PMUY Portal</p>
      <div className="mt-4">
        <Link to="/apply" className="btn hero-btn me-3">Apply Now</Link>
        <Link to="/status" className="btn btn-outline-light hero-btn-outline">Check Status</Link>
      </div>
    </div>
  );
};

export default Home;
