// src/components/ApplyForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ApplyForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    aadhaar: '',
    address: '',
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/apply', formData);
      alert("Application submitted successfully!");
    } catch (error) {
      console.error(error);
      alert("Submission failed");
    }
  };

  return (
    <div className="card p-4 mx-auto" style={{maxWidth: '500px'}}>
      <h2 className="mb-4 text-center">Apply Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Name</label>
          <input type="text" name="name" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Aadhaar Number</label>
          <input type="text" name="aadhaar" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Address</label>
          <textarea name="address" className="form-control" onChange={handleChange} required />
        </div>
        <button type="submit" className="btn btn-success w-100">Submit</button>
      </form>
    </div>
  );
};

export default ApplyForm;
