import React, { useState } from 'react';
import axios from 'axios';
import './ApplyForm.css';

function ApplyForm() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    aadhar: '',
    address: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/apply', formData);
      alert('Application submitted successfully!');
      setFormData({ name: '', email: '', phone: '', aadhar: '', address: '' });
    } catch (error) {
      alert('Error submitting application!');
    }
  };

  return (
    <div className="apply-form">
      <h2>Apply for PMUY</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input type="text" name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} required />
        <input type="text" name="aadhar" placeholder="Aadhar" value={formData.aadhar} onChange={handleChange} required />
        <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default ApplyForm;
